async function t(a){const e=a.replace(/\D/g,"");if(e.length!==8)throw new Error("CEP deve ter 8 dígitos");try{const o=await(await fetch(`https://viacep.com.br/ws/${e}/json/`)).json();if(o.erro)throw new Error("CEP não encontrado");return{cep:o.cep,logradouro:o.logradouro||"",complemento:o.complemento||"",bairro:o.bairro||"",cidade:o.localidade||"",estado:o.uf||"",enderecoCompleto:`${o.logradouro||""}, ${o.bairro||""}, ${o.localidade||""} - ${o.uf||""}`.trim()}}catch(r){throw console.error("Erro ao buscar CEP:",r),r}}export{t as b};
